import axios from "axios";
import { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import Card from "../components/CardsCharacters";


function Character() {

const[characters, setCharacters] = useState([]);
//const[films, setFilms] = useState([]);
//const[planets, setPlanets] = useState([]);
  
      const getDataApi = async (characters) => {

          const response = await axios.get(
              `https://rickandmortyapi.com/api/character/?page=2`);
          //const { res } = response.data
           // console.log(res);
              setCharacters(response.data.results);
              //console.log(response.data.results);
          }
    
      const renderCharacters = () => {

        return characters.map((characters, info) => (
        <article key={info}>
          <h4>{characters.name}</h4>
          <Link to={`/character`}>
            <Card item={characters} />
          </Link>
        </article>)
      )}

      useEffect(() => {
        getDataApi();
    },[])

      return (

      <section>
        <h1>Rick&Morty</h1>
        <select name="" id="" onChange={() => getDataApi({})}>
            <option value="characters">personajes</option>
        </select>

        <div>
          {renderCharacters()}
         </div>
      </section>
);
}

export default Character
