import {useForm} from 'react-hook-form'
import { useLocation } from 'react-router-dom'
import { useState } from 'react'

const prevState = {
    email: "Zorahida@zorahida.com",
    password:"Hola"
}
 const Login = ({ LoginUser, logError}) => {
    const location = useLocation()
    const { state } = location
 
const [form, setForm] = useState(prevState)
 
    const dataUser = (ev) => {
        const { value, name } = ev.target;
        setForm({...form, [name]: value })
    
    const submitForm = (ev) => {
        ev.preventDefault();
        LoginUser(form, state ? state.prevRoute: null)
        setForm(prevState);
    }
    
    return (<form onSubmit={submitForm}>
        <label htmlFor="email">
            Email
            <input type="email" name="email" id="email" onChange={dataUser} value={form.email} />
        </label>
        <label htmlFor="pass">
            Contrase√±a
            <input type="password" name="password" id="pass" onChange={dataUser}
                value={form.password} />
        </label>
        <div>
            <button type="submit">Log in</button>
        </div>

        {logError ? <div style={{ color: 'red' }}>{logError} </div> : null}
    </form>)
    
    }}
export default Login
