import axios from "axios";
import { useState, useEffect } from "react";

const Episodes = ({ episodes }) => {

    const[listEpisodes, setListEpisodes] = useState([]);
    const[episodeFilter, setEpisodeFilter] = useState("");

    const getAllEpisodes = async () => {

    const response = await axios.get(
            `https://rickandmortyapi.com/api/episode/?page=2"`);

            setListEpisodes(response.data.results);
            //console.log(response.data.results);
        };

        useEffect(() => {
            getAllEpisodes();
        }, []);

    const renderEpisodes = () => {
        return listEpisodes.map((episode, i) => (
           <div key= {i}>
             <h4>{episode.name}</h4>
            <img src={episode.image} alt={episode.name} />
        </div>
        ));
    };

    const handleFilterChange = (e) => {
        setEpisodeFilter(e.target.value);
    };

    const filteredEpisodes = listEpisodes.filter((episode)=>
   episode.name.toLoweCase().includes(episodeFilter.toLowerCase()) 
   );

   return (
    <div>
        <h2>Ricky&Morty</h2>
        <input
        type="text"
        value={episodeFilter}
        onChange={handleFilterChange} />
        {filteresEpisodes.length > 0 ? (renderepisodes() ) : (<p>Error</p>)}
    </div>
   );
};

export default Episodes;
